import React from "react";
import cassette from "./cassette-tape.png";
import "../assets/App.css";

export const Description = (props) => {
  const [Chapter, Name, Description] = props.current;
  return (
    <div className="cassette-wrapper">
      <span className="chapter">{Chapter}</span>
      <img src={cassette} className="cassette" alt="cassette"></img>
      <div className="description">
        <div className="title">{Name}</div>
        <div className="text">
          <strong>
            {Description}
            <br></br>Tell me what you think :)
          </strong>
        </div>
      </div>
    </div>
  );
};
