import React from "react";
import styled from "styled-components";
import dennis from './dennis.jpg'
const Wrapper = styled.div`
  min-height: 80vh;
  background-color: #24305e;
  padding: 15px;
  font-family: "Anton", sans-serif;
`;
const Card = styled.div`
  position: relative;
  width: 100%;
  background-color:${props => props.instagram? "#f44336":"#009688"};
  color: black;
  margin: 20px auto;
  padding: 2px 0;
  border-radius: 6px;
  filter: drop-shadow(5px 5px 5px black);
`;
const Text = styled.div`
  margin: 10px auto;
  width: 96%;
`;
const Instagram = styled.div`

`

export default function About() {
  return (
    <Wrapper>
      <Card>
        <Img src={dennis}></Img>
      </Card>
      <Card>
        <Text>
          Hey, I'm Dennis.<br/> Tape is a small project to house all my playlists in one
          place made with ReactJS and hosted on Github pages. DM me for bugs.
        </Text>
      </Card>
      <Card instagram>
        <Instagram><a href="https://www.instagram.com/dennisjohnson_music/?hl=en" rel="noopener"></a></Instagram>
      </Card>
    </Wrapper>
  );
}
