import React from "react";
import cassette from "./cassette-tape.png";
import "./App.css";

export default function Description(props) {
  return (
    <div className="cassette-wrapper">
      <span className="chapter">{props.current.Chapter}</span>
      <img src={cassette} className="cassette" alt="cassette"></img>
      <div className="description">
        <div className="title">{props.Name}</div>
        <div className="text">
          <strong>
            Sorry I'm late this week. I put together a melting pot of quirky,
            upbeat Alternative RnB/Jazz/Soul and what not. Some of these would
            definitely seem 'out there' for some, but give them a chance! Some
            of them took me a few listens to catch on to too. <br></br>Tell me
            what you think :)
          </strong>
        </div>
      </div>
    </div>
  );
}
